figma.ui.onmessage = async (msg: any) => {
    try {
        if (msg.type === "sync-tokens") {
            // TODO: call your existing sync
            figma.notify("âœ… Tokens synced");
            figma.ui.postMessage({ type: "notify", text: "Tokens synced" });
            return;
        }

        if (msg.type === "generate-screens") {
            // TODO: call your generate
            figma.notify("ðŸ§± Screens generated");
            figma.ui.postMessage({ type: "notify", text: "Screens generated" });
            return;
        }

        if (msg.type === "update-screens") {
            // TODO: call your update
            figma.notify("â™»ï¸ Screens updated");
            figma.ui.postMessage({ type: "notify", text: "Screens updated" });
            return;
        }

        if (msg.type === "export-variables") {
            const data = exportVariablesJSON();
            figma.ui.postMessage({ type: "export-result", payload: data });
            figma.notify("ðŸ“¤ Variables exported");
            return;
        }

        if (msg.type === "export-selection") {
            const data = exportSelectionJSON({ onlyFrames: true, maxDepth: 3 });
            figma.ui.postMessage({ type: "export-result", payload: data });
            figma.notify("ðŸ“¤ Selection exported");
            return;
        }

        if (msg.type === "export-document") {
            const data = exportDocumentJSON({ onlyFrames: true, maxDepth: 2, maxChildren: 300 });
            figma.ui.postMessage({ type: "export-result", payload: data });
            figma.notify("ðŸ“¤ Document exported");
            return;
        }
    } catch (e: any) {
        figma.notify(`âš ï¸ ${e?.message || e}`);
        figma.ui.postMessage({ type: "notify", text: `Error: ${e?.message || e}` });
    }
};

const send = (msg: any) => parent.postMessage({ pluginMessage: msg }, "*");
const out = document.getElementById("out") as HTMLTextAreaElement;
const statusEl = document.getElementById("status")!;

function setStatus(s: string) { statusEl.textContent = s; }

(document.getElementById("syncTokens")!).onclick = () => send({ type: "sync-tokens" });
(document.getElementById("genScreens")!).onclick   = () => send({ type: "generate-screens" });
(document.getElementById("updateScreens")!).onclick= () => send({ type: "update-screens" });

(document.getElementById("exportVars")!).onclick      = () => { setStatus("Exporting variablesâ€¦");  send({ type: "export-variables" }); };
(document.getElementById("exportSelection")!).onclick = () => { setStatus("Exporting selectionâ€¦");  send({ type: "export-selection" }); };
(document.getElementById("exportDoc")!).onclick       = () => { setStatus("Exporting documentâ€¦");   send({ type: "export-document"  }); };

window.onmessage = (e) => {
    const msg = e.data?.pluginMessage;
    if (!msg) return;
    if (msg.type === "export-result") {
        out.value = JSON.stringify(msg.payload, null, 2);
        setStatus("Ready");
    }
    if (msg.type === "notify") setStatus(msg.text || "Ready");
};
