import { exportTokenSchemaFromCollection } from "./core/export";

figma.showUI(__html__, { width: 980, height: 620 });

figma.notify("Plugin booted");


/** Mirror logs into the UI panel and also to the code console. */
function debug(...args: any[]) {
    try {
        figma.ui.postMessage({
            type: "debug",
            lines: args.map(a => {
                if (typeof a === "string") return a;
                try { return JSON.stringify(a); } catch (_e) { return String(a); }
            }),
            ts: Date.now(),
        });
    } catch (_e) {}
    try { console.log(...args); } catch (_e) {}
}

debug("🔌 plugin booted");
figma.ui.postMessage({ type: "ready" });

figma.ui.onmessage = async (msg: any) => {
    try {
        debug("[code] got", msg?.type);

        switch (msg.type) {
            case "ping": {
                figma.ui.postMessage({ type: "pong" });
                figma.ui.postMessage({ type: "echo", text: "hello-from-code" });

                const json = exportTokenSchemaFromCollection("YY_web");
                figma.ui.postMessage({ type: "variables:pulled", json });
                debug("[code] sent variables:pulled (bootstrap)");
                return;
            }

            case "pull-variables": {
                const data = exportTokenSchemaFromCollection(msg.collection || "YY_web");
                figma.ui.postMessage({ type: "variables:pulled", json: data });
                figma.notify("📥 Variables pulled");
                debug("[code] pulled", { count: Object.keys(data?.tokens ?? {}).length });
                return;
            }

            case "resize-ui": {
                const w = Math.max(240, Math.min(1600, Number(msg.width) || 980));
                const h = Math.max(200, Math.min(1200, Number(msg.height) || 620));
                figma.ui.resize(w, h);
                debug("[code] resized UI →", `${w}×${h}`);
                return;
            }
        }
    } catch (e: any) {
        debug("[code] error", e?.message || e);
        figma.notify(`⚠️ ${e?.message || e}`);
        figma.ui.postMessage({ type: "notify", text: `Error: ${e?.message || e}` });
    }
};

// src/ui.ts
const log = (...a: any[]) => console.log("[ui]", ...a);

window.addEventListener("DOMContentLoaded", () => {
    log("DOMContentLoaded");

    const send = (msg: any) => {
        log("postMessage →", msg);
        parent.postMessage({ pluginMessage: msg }, "*");
    };

    const out = document.getElementById("out") as HTMLTextAreaElement | null;
    const logBox = document.getElementById("log") as HTMLTextAreaElement | null;
    const statusEl = document.getElementById("status") as HTMLElement | null;
    const pullBtn = document.getElementById("pullVars");
    const resizeBtn = document.getElementById("resize");
    const wEl = document.getElementById("w") as HTMLInputElement | null;
    const hEl = document.getElementById("h") as HTMLInputElement | null;

    const setStatus = (s: string) => { if (statusEl) statusEl.textContent = s; log("status:", s); };

    const appendLog = (lines: string[]) => {
        if (!logBox) return;
        const time = new Date().toLocaleTimeString();
        const text = lines.map(l => `[${time}] ${l}`).join("\n") + "\n";
        logBox.value += text;
        if (logBox.value.length > 5000) logBox.value = logBox.value.slice(-5000);
        logBox.scrollTop = logBox.scrollHeight;
    };

    if (!pullBtn || !resizeBtn || !wEl || !hEl || !out || !statusEl || !logBox) {
        appendLog(["❌ Missing UI elements (check IDs in ui.html)."]);
        return;
    }

    // handshake — code replies with pong and auto-pull once
    send({ type: "ping" });

    pullBtn.addEventListener("click", () => {
        setStatus("Pulling variables…");
        send({ type: "pull-variables", collection: "YY_web" });
    });

    resizeBtn.addEventListener("click", () => {
        const w = parseInt(wEl.value || "980", 10);
        const h = parseInt(hEl.value || "620", 10);
        setStatus(`Resizing to ${w}×${h}…`);
        send({ type: "resize-ui", width: w, height: h });
    });

    (window as any).onmessage = (e: MessageEvent) => {
        const msg = (e.data && (e.data as any).pluginMessage) || null;
        if (!msg) return;
        log("← pluginMessage", msg);

        if (msg.type === "ready") { setStatus("Connected"); return; }
        if (msg.type === "pong")  { setStatus("Loaded"); return; }
        if (msg.type === "echo")  { appendLog(["echo from code"]); return; }

        if (msg.type === "variables:pulled") {
            out.value = JSON.stringify(msg.json, null, 2);
            setStatus("Ready");
            appendLog(["variables pulled"]);
            return;
        }
        if (msg.type === "notify") {
            setStatus(msg.text || "Ready");
            appendLog([`notify: ${msg.text}`]);
            return;
        }
        if (msg.type === "debug") {
            const { lines = [] } = msg;
            appendLog(Array.isArray(lines) ? lines : [String(lines)]);
            return;
        }
    };
});
